import axios from 'axios';
import React, {useState} from "react"
// thunks

// Redux is synchronous
// we need to perform an async operation
export const fetchQuote = () => {
  return dispatch => {
    dispatch({ type: 'FETCH_QUOTE_START' });
    axios
      .get(`https://programming-quotes-api.herokuapp.com/quotes/lang/en`)
      .then(res => {
        console.log("what is this",res.data)
        dispatch({ type: 'FETCH_QUOTE_SUCCESS', payload: res.data });
      })
      .catch(err => {
        // message: err.response.data
        // status: err.response.status
        dispatch({
          type: 'FETCH_QUOTE_FAILURE',
          payload: `Error ${err.response}: ${err.response}`
        });
      });
  };
};

